import React, { useState, useEffect } from 'react';
import { useParams } from "react-router-dom";

function MovieDetails({ match }) {
  const [movieDetails, setMovieDetails] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const { id } = useParams;

    fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=4a54ab11d3721dac3c7f819587e24e14`)
      .then((response) => response.json())
      .then((data) => {
        setMovieDetails(data);
        setIsLoading(false);
      })
      .catch((err) => {
        setError('Une erreur s\'est produite lors du chargement des détails du film.');
        setIsLoading(false);
      });
  }, [match.params]);

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className="details">
      <h1>{movieDetails.title}</h1>
      <img
        src={`https://image.tmdb.org/t/p/w500${movieDetails.poster_path}`}
        alt={movieDetails.title}
      />
      <p>{movieDetails.overview}</p>
      <p>Vote moyen : {movieDetails.vote_average}</p>
      <p>Date de sortie : {movieDetails.release_date}</p>
    </div>
  );
}

export default MovieDetails;
