import React, { useState, useEffect } from 'react';
import './movieDetails.css';

function MovieDetails({ match }) {
  const [movieDetails, setMovieDetails] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const id = localStorage.getItem('id');

    fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=4a54ab11d3721dac3c7f819587e24e14`)
      .then((response) => response.json())
      .then((data) => {
        setMovieDetails(data);
        setIsLoading(false);
      })
      .catch((err) => {
        setError('Une erreur s\'est produite lors du chargement des détails du film.');
        setIsLoading(false);
      });
  });

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div className='details-container'>
        <h1>{movieDetails.title}</h1>
        <div className="grid">
            <div className='left-column'>
                <img
                    src={`https://image.tmdb.org/t/p/w500${movieDetails.poster_path}`}
                    alt={movieDetails.title}
                />
            </div>
            <div className='right-column'>
                <p>Budget : ${movieDetails.budget.toLocaleString()}</p>
                <p>Revenue : ${movieDetails.revenue.toLocaleString()}</p>
                <p>Bénéfice : ${(movieDetails.revenue - movieDetails.budget).toLocaleString()}</p>
                <p>Runtime : {movieDetails.runtime} minutes</p>
                <a href={movieDetails.homepage} target='_blank' rel="noreferrer">Barbie website</a>
                    {movieDetails.production_companies.map((company) => (
                        <div key={company.id}>
                            <p>{company.name} ({company.origin_country})</p>
                        </div>
                    ))}
                <p>{movieDetails.overview}</p>
                <p>Note : {movieDetails.vote_average}</p>
                <p>Realease Date : {movieDetails.release_date}</p>
            </div>
        </div>
    </div>
  );
}

export default MovieDetails;
