import React, { useState, useEffect } from 'react';
import './movieDetails.css';

function MovieDetails({ match }) {
  const [movieDetails, setMovieDetails] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const id = localStorage.getItem('id');

    fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=4a54ab11d3721dac3c7f819587e24e14`)
      .then((response) => response.json())
      .then((data) => {
        setMovieDetails(data);
        setIsLoading(false);
      })
      .catch((err) => {
        setError('Une erreur s\'est produite lors du chargement des détails du film.');
        setIsLoading(false);
      });
  });

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  function convertMinutesToHours(runtime) {
    const hours = Math.floor(minutes / 60);
    const minutes = minutes % 60;
    return `${hours}h ${mi}min`;
  }

  return (
    <div className='details-container'>
        <div className="grid-container">
            <div className='left-column'>
                <img
                    src={`https://image.tmdb.org/t/p/w500${movieDetails.poster_path}`}
                    alt={movieDetails.title}
                    className='poster'
                />
            </div>
            <div className='right-column'>
                <h1>{movieDetails.title}</h1>
                <div>
                    <p>{movieDetails.release_date}</p>
                    {movieDetails.genres.map((genre) => (
                        <div key={genre.id} className='container-genre'>
                            <p>{genre.name}</p>
                        </div>
                    ))}
                    <p><span>Runtime</span> : {movieDetails.runtime} minutes</p>
                </div>
                <p><span>Budget</span> : ${movieDetails.budget.toLocaleString()}</p>
                <p><span>Revenue</span> : ${movieDetails.revenue.toLocaleString()}</p>
                <p><span>Bénéfice</span> : ${(movieDetails.revenue - movieDetails.budget).toLocaleString()}</p>
                <p><span>Runtime</span> : {movieDetails.runtime} minutes</p>
                <a href={movieDetails.homepage} target='_blank' rel="noreferrer">Barbie website</a>
                {movieDetails.production_companies.map((company) => (
                    <div key={company.id}>
                        <p>{company.name} ({company.origin_country})</p>
                    </div>
                ))}
                <p>{movieDetails.overview}</p>
                <p><span>Note</span> : {movieDetails.vote_average}</p>
            </div>
        </div>
    </div>
  );
}

export default MovieDetails;
