import React, { Component } from "react";
import './App.css'

const API = 'https://api.themoviedb.org/3/trending/all/day?api_key=4a54ab11d3721dac3c7f819587e24e14';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
    }
  }

  componentDidMount() {
    fetch(API)
    .then(res => res.json())
    .then(json => {
      this.setState({
        isLoaded: true,
        items: json.results,
      })
    });
  }

  render() {
    let { isLoaded, items } = this.state;
    console.log(items);

    if (!isLoaded) {
      return <div>Loading...</div>
    } else {
      return (
        <div className="app">
          <h1>Movies Trending this day</h1>
          <div>

          </div>

        </div>
      );
    }
  }
}

export default App;
